mapper.select:
SELECT id, name, fee, autopay, support_split_payment, version
FROM payment_gateway_provider
WHERE id = #{id}

mapper.update:
UPDATE payment_gateway_provider
name = #{name}, fee = #{fee}, autopay = #{autoPay}, support_split_payment = #{supportSplitPayment},
WHERE id = #{id}

mapper.select:
SELECT id, name, fee, autopay, support_split_payment, version
FROM payment_gateway_provider
WHERE support_split_payment = #{supportSplitPayment}
  AND autopay = #{autoPay}
  AND name = #{name}
